function Restaurar(n){let t=[],r=[],i="Deseja restaurar ";if(n)i+=" todos os campos do Membro?";else if($(".checks").each((n,i)=>{$(i).prop("checked")==!0&&(t.push($(i).val()),r.push($(i).data("descr")))}),t.length==1)i+=`o campo ${r[0]}?`;else if(t.length>1)i+=`os campos abaixo? <br /> ${r.join("<br />")}`;else{$.alertError("Restaurar Informações","Favor selecionar os campos a serem restaurados!");return}$.showYesNoAlert(i,"Membro",function(){var n={membroId:$("#Id").val(),campos:t.join(",")};$.ajaxPost(urlRestaurarMembro,n,function(n){n.status==="OK"?closeAndRefresh():$.alertError("Membro",n.mensagem)})})}function closeAndRefresh(){parent.location.reload();FecharModal()}function FecharModal(){parent.$.fancybox.close()}$(()=>{$("#btnRestaurar").click(()=>{Restaurar(!1)}),$("#btnRestaurarTodos").click(()=>{Restaurar(!0)})});